Explosion::Explosion(int col, int row, std::function<void()> callback)
: horizonPosition(col - explosionWidth / 2)
, verticalPosition(row - explosionHeight / 4)
, animationCount(0)
, finishedCallback(callback)
{
}

void Explosion::input(int key)
{
}

std::array<std::array<DisplayInfo, windowWidth>, windowHeight>& Explosion::simulate()
{
    rectangle.left = horizonPosition;
    rectangle.right = horizonPosition + explosionWidth;
    rectangle.top = verticalPosition;
    rectangle.bottom = verticalPosition + explosionHeight;
    for(auto row = 0 ; row < current.size() ; row++) {
        auto rows = current.at(row);
        for(auto col = 0 ; col < rows.size() ; col++) {
            auto explosionCol = col - rectangle.left;
            auto explosionRow = row - rectangle.top;
            if(rectangle.left <= col
               && col < rectangle.right
               && rectangle.top <= row
               && row < rectangle.bottom) {
                auto e = explosion.at(animationCount);
                current.at(row).at(col) = e.at(explosionRow).at(explosionCol);
            } else {
                current.at(row).at(col) = white;
            }
        }
    }
    auto old = animationCount;
    animationCount = (animationCount + 1) % explosion.size();
    if(old > animationCount) finishedCallback();
    return current;
}



