std::array<std::array<std::array<DisplayInfo, windowWidth>, windowHeight>, 2> openingAnime = {kuusha,kuusha};
std::array<std::array<std::array<DisplayInfo, windowWidth>, windowHeight>, 7> startAnime = {
    hajime, hajime, geisha, geisha, hajime, start, start
};

void OpeningStage::input(int key)
{
    if(!isStart) {
        isStart = true;
        animeCount = 0;
    }
}

std::array<std::array<DisplayInfo, windowWidth>, windowHeight>& OpeningStage::simulate()
{
    if(isStart) {
        animeCount = ++animeCount % startAnime.size();
        if(animeCount == 0){
            superSign()->setStage(std::make_shared<GameStage>(superSign(), 3));
        }
        return startAnime[animeCount];
    } else {
        animeCount = ++animeCount % openingAnime.size();
        return openingAnime[animeCount];
    }
}
